<?php get_header(); ?>		<div class="row">		<div class="twelvecol">			<?php if (function_exists('breadcrumbs')) breadcrumbs(); ?>		</div>	</div>		<div class="row">		<div class="eightcol" id="main">						<div id="content" role="main">										<?php if ( have_posts() ) : ?>						<?php while ( have_posts() ) : the_post(); ?>											<?php get_template_part( 'content' ); ?>						<?php endwhile; // end of the loop. ?>										<?php else : ?>											<article id="post-0" class="post no-results not-found">							<header class="entry-header-search">								<h1 class="entry-title">Nothing Found</h1>							</header>														<div class="entry-content">								<p>Sorry, nothing match your search criteria. Please try again with some different keywords.</p>								<p><?php get_search_form(); ?></p>							</div>													</article><!-- #post-0 -->					<?php endif; ?>										<nav class="paging">						<?php if(function_exists('kriesi_pagination')) : kriesi_pagination(); else:  ?>						<?php global $wp_query; $total_pages = $wp_query->max_num_pages; if ( $total_pages > 1 ) { ?>							<div id="nav-below" class="navigation">								<div class="nav-previous"><?php next_posts_link(__( '<span class="meta-nav">&laquo;</span> Older posts', 'newzeo' )) ?></div>								<div class="nav-next"><?php previous_posts_link(__( 'Newer posts <span class="meta-nav">&raquo;</span>', 'newzeo' )) ?></div>							</div>						<?php } endif; ?>					</nav>				</div> <!-- content end -->						</div> <!-- eightcol end -->				<div class="fourcol last" id="sidebar">			<?php get_sidebar(); ?>		</div>			</div> <!-- row end --><?php get_footer(); ?>